mapper.update:
update			order_detail					set
				od_count = #{count},
				od_price = #{price}

				where od_id = #{od_id}

mapper.insert:

INSERT INTO orders (
	user_id, 
	total_price, 
	o_date
) VALUES (
	#{user_id}, 
	#{total_price}, 
	#{date}
)

mapper.select:
SELECT tb.od_id, tb.od_price, tb.od_count, tb.p_id, tb.o_id, ta.o_date, ta.o_id, tc.user_name, tc.user_address, tc.user_phone, td.o_total_price, te.p_name, tf.pi_image, tf.pi_unique
FROM orders ta LEFT JOIN order_detail tb ON (ta.o_id = tb.o_id) LEFT JOIN user tc ON (ta.user_id = tc.user_id) LEFT JOIN ( SELECT ta.o_id, SUM(tb.od_price*tb.od_count) o_total_price FROM orders ta LEFT JOIN order_detail tb ON (ta.o_id = tb.o_id) GROUP BY ta.o_id ) td ON ta.o_id = td.o_id LEFT JOIN product te ON (tb.p_id = te.p_id) LEFT JOIN product_img tf ON (te.p_id = tf.p_id)
WHERE ta.o_id = #{id}

SELECT tb.od_id, tb.od_price, tb.od_count, tb.p_id, tb.o_id, ta.o_date, ta.o_id, ta.user_id, tc.user_name, tc.user_address, tc.user_phone, td.o_total_price, te.p_name, tf.pi_image, tf.pi_unique
FROM orders ta LEFT JOIN order_detail tb ON (ta.o_id = tb.o_id) LEFT JOIN user tc ON (ta.user_id = tc.user_id) LEFT JOIN ( SELECT ta.o_id, SUM(tb.od_price*tb.od_count) o_total_price FROM orders ta LEFT JOIN order_detail tb ON (ta.o_id = tb.o_id) GROUP BY ta.o_id ) td ON ta.o_id = td.o_id LEFT JOIN product te ON (tb.p_id = te.p_id) LEFT JOIN product_img tf ON (te.p_id = tf.p_id)
WHERE ta.user_id = #{user_id}

mapper.insert:

	INSERT INTO orders (
		user_id
	) VALUES (
		#{user_id}
	)

        SELECT LAST_INSERT_ID()
